import React, { useCallback, useEffect } from 'react';
import './App.css';
import { Block } from './Block';

function formatDate(date) {
  const day = date.getDate().toString().padStart(2, '0');
  const month = (date.getMonth() + 1).toString().padStart(2, '0');
  const year = date.getFullYear();
  return `${day}.${month}.${year}`;
}

function App() {
  const [fromCurrency, setFromCurrency] = React.useState('RUB');
  const [toCurrency, setToCurrency] = React.useState('USD');
  const [toPrice, setToPrice] = React.useState(1);
  const [fromPrice, setFromPrice] = React.useState(0);
  const ratesRef = React.useRef({});

  const onChangeFromPrice = useCallback((value) => {
    const price = value / ratesRef.current[fromCurrency];
    const result = price * ratesRef.current[toCurrency];
    setToPrice(result.toFixed(3));
    setFromPrice(value);
  }, [fromCurrency, toCurrency]);

  const onChangeToPrice = useCallback((value) => {
    const result =
      (ratesRef.current[fromCurrency] / ratesRef.current[toCurrency]) * value;
    setFromPrice(result.toFixed(3));
    setToPrice(value);
  }, [fromCurrency, toCurrency]);

  useEffect(() => {
    const currentDate = new Date();
    const formattedDate = formatDate(currentDate);
    const proxyUrl = 'http://localhost:8080/'; // Use the address of your deployed CORS Anywhere proxy

    fetch(`${proxyUrl}https://cbar.az/currencies/${formattedDate}.xml`)
      .then((res) => res.text())
      .then((xmlData) => {
        // Parse the XML data using DOMParser
        const parser = new DOMParser();
        const xmlDoc = parser.parseFromString(xmlData, 'text/xml');
        const rates = {};
        const currencies = xmlDoc.getElementsByTagName('Valute');
        for (let i = 0; i < currencies.length; i++) {
          const currencyCode = currencies[i].getAttribute('Code');
          const rate = parseFloat(currencies[i].getElementsByTagName('Value')[0].textContent);
          rates[currencyCode] = rate;
        }
        ratesRef.current = rates;
        onChangeToPrice(1);
      })
      .catch((err) => {
        console.warn(err);
        alert('Failed to get information');
      });
  }, [onChangeToPrice]);
  React.useEffect(() => {
    onChangeFromPrice(fromPrice);
  }, [fromCurrency, fromPrice, onChangeFromPrice]);

  React.useEffect(() => {
    onChangeToPrice(toPrice);
  }, [toCurrency, toPrice, onChangeToPrice]);

  return (
    <div className="App">
      <Block
        value={fromPrice}
        currency={fromCurrency}
        onChangeCurrency={setFromCurrency}
        onChangeValue={onChangeFromPrice}
      />
      <Block
        value={toPrice}
        currency={toCurrency}
        onChangeCurrency={setToCurrency}
        onChangeValue={onChangeToPrice}
      />
    </div>
  );
}

export default App;
