import React, { useCallback, useEffect, useState } from 'react';
import './App.css';
import { Block } from './Block';
import { parse } from 'fast-xml-parser';
function App() {
  const [fromCurrency, setFromCurrency] = useState('RUB');
  const [toCurrency, setToCurrency] = useState('USD');
  const [toPrice, setToPrice] = useState(1);
  const [fromPrice, setFromPrice] = useState(0);
  const ratesRef = React.useRef({});

  const onChangeFromPrice = useCallback((value) => {
    const price = value / ratesRef.current[fromCurrency];
    const result = price * ratesRef.current[toCurrency];
    setToPrice(result.toFixed(3));
    setFromPrice(value);
  }, [fromCurrency, toCurrency]);

  const onChangeToPrice = useCallback((value) => {
    const result =
      (ratesRef.current[fromCurrency] / ratesRef.current[toCurrency]) * value;
    setFromPrice(result.toFixed(3));
    setToPrice(value);
  }, [fromCurrency, toCurrency]);

  useEffect(() => {
    fetch(`http://localhost:3000/currencies`)
      .then((res) => res.text())
      .then((data) => {
        const options = {
          ignoreAttributes: false,
          attributeNamePrefix: '',
        };

        const result = parse(data, options);
        if (result && result.ValCurs && result.ValCurs.Valute) {
          const rates = {};
          result.ValCurs.Valute.forEach((valute) => {
            rates[valute.CharCode] = parseFloat(valute.Value.replace(',', '.'));
          });
          ratesRef.current = rates;
          onChangeToPrice(1);
        } else {
          console.warn('Failed to parse XML data');
          alert('Failed to get information');
        }
      })
      .catch((err) => {
        console.warn(err);
        alert('Failed to get information');
      });
  }, [onChangeToPrice]);

  React.useEffect(() => {
    onChangeFromPrice(fromPrice);
  }, [fromCurrency, fromPrice, onChangeFromPrice]);

  React.useEffect(() => {
    onChangeToPrice(toPrice);
  }, [toCurrency, toPrice, onChangeToPrice]);

  return (
    <div className="App">
      <Block
        value={fromPrice}
        currency={fromCurrency}
        onChangeCurrency={setFromCurrency}
        onChangeValue={onChangeFromPrice}
      />
      <Block
        value={toPrice}
        currency={toCurrency}
        onChangeCurrency={setToCurrency}
        onChangeValue={onChangeToPrice}
      />
    </div>
  );
}

export default App;
